<?phpabstract class notes{		/**	 * Add a message to the "popnotes" session.	 * @param String Type of message.	 * @param String Message	 * @return void	 */	public static function add($type, $note, $redirect = false)	{		$popnotes = Session::get('popnotes');		if(!$popnotes)			$popnotes = array();		$content = Array('type' => $type, 'note' => $note);		$popnotes[] = $content;		Session::flash('popnotes', $popnotes);	}		public static function fetch()	{		$messages = Session::get('popnotes');		if(isset($messages) && !empty($messages))		{			Session::forget('popnotes');			return $messages;		}		return false;	}		public static function count()	{			}		public static function achievement($note, $redirect = false)	{		self::add('achievement', $note, $redirect);	}		public static function error($note, $redirect = false)	{		self::add('danger', $note, $redirect);			}	public static function success($note, $redirect = false)	{		self::add('success', $note, $redirect);	}	public static function info($note, $redirect = false)	{		self::add('info', $note, $redirect);	}	}